:root {
  --light: #e6e5ea;
  --grey: #817d92;
  --dark: #24232c;
  --black: #18171f;
  --primary: #a4ffaf;
  --caution: #f8cd65;
  --warning: #fb7c58;
  --danger: #f64a4a;
}

.bar.fill-primary {
  background-color: var(--primary);
  border-color: var(--primary);
}
.bar.fill-caution {
  background-color: var(--caution);
  border-color: var(--caution);
}
.bar.fill-warning {
  background-color: var(--warning);
  border-color: var(--warning);
}
.bar.fill-danger {
  background-color: var(--danger);
  border-color: var(--danger);
}
.bar.fill-blank {
  background-color: none;
  border-color: var(--light);
}

body {
  font-family: "JetBrains Mono", sans-serif;
  margin: 0;
  padding: 0;
  background-color: var(--black);
  color: var(--light);
  font-size: 16px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  height: 100vh;
}

.container {
  margin: 0 auto;
  width: 100%;
  max-width: 540px;
}

.password,
form {
  background-color: var(--dark);
  padding: 1rem;
}

/* TITLE */
.app-title {
  font-size: 1rem;
  color: var(--grey);
  margin-bottom: 1rem;
}

/* PASSWORD */
.password {
  display: flex;
  align-items: center;
  margin-bottom: 25px;
}

.password > input {
  background-color: transparent;
  border: none;
  outline: var(--primary);
  color: var(--light);
  font-family: inherit;
  font-size: 1.5rem;
  width: 70%;
  margin-right: auto;
}

.password > input:focus {
  border-bottom: 0.5px solid var(--primary);
}

.copy-text {
  color: var(--primary);
  margin-right: 1rem;
}
.hidden {
  display: none;
}

#copy-icon:hover {
  cursor: pointer;
  filter: brightness(450%) contrast(101%);
}
/* FORM */

/* slider */
.slider {
  margin-bottom: 32px;
}
.slider-label {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.slider-label span {
  color: var(--primary);
  font-size: 1.5rem;
}

input[type="range"] {
  -moz-appearance: none;
  -ms-apperance: none;
  -webkit-apperance: none;
  appearance: none;
  background: transparent;
  outline: none;
  width: 100%;
  cursor: pointer;
  margin-top: 18px;
}

/* track */
input[type="range"]::-moz-range-track {
  background-color: var(--black);
  height: 8px;
}
input[type="range"]::-ms-track {
  background-color: var(--black);
  height: 8px;
}
input[type="range"]::-webkit-slider-runnable-track {
  background-color: var(--black);
  height: 8px;
}

/* thumb */
input[type="range"]::-moz-range-thumb {
  position: relative;
  bottom: 10px;
  appearance: none;
  width: 28px;
  height: 28px;
  background-color: var(--light);
  border-radius: 50%;
  z-index: 2;
}
input[type="range"]::-ms-thumb {
  position: relative;
  bottom: 10px;
  appearance: none;
  width: 28px;
  height: 28px;
  background-color: var(--light);
  border-radius: 50%;
  z-index: 2;
}
input[type="range"]::-webkit-slider-thumb {
  position: relative;
  bottom: 10px;
  appearance: none;
  width: 28px;
  height: 28px;
  background-color: var(--light);
  border-radius: 50%;
  z-index: 2;
}

/* thumb hover */
input[type="range"]::-webkit-slider-thumb:hover {
  border: 2px solid var(--primary);
  background-color: var(--black);
}
input[type="range"]::-moz-range-thumb:hover {
  border: 2px solid var(--primary);
  background-color: var(--black);
}
input[type="range"]::-ms-thumb:hover {
  border: 2px solid var(--primary);
  background-color: var(--black);
}

/* progress bar */
.slider-wrapper {
  position: relative;
}

#sliderProgress {
  position: absolute;
  top: 1.15rem;
  background-color: var(--primary);
  height: 8px;
  /* width: 33.33333333336%; */
  width: 50%;
}

/* number input */
input[type="number"] {
  -webkit-appearance: textfield;
  -moz-appearance: textfield;
  appearance: textfield;
  background: transparent;
  border: none;
  font-size: 1.5rem;
  font-family: "JetBrains Mono";
  color: var(--primary);
  width: 3.2rem;
}

input[type="number"]:focus {
  border-bottom: 1px solid var(--primary);
  outline: none;
}

/* CHECKBOXES */
.checkboxes label {
  display: block;
  position: relative;
  margin-left: 40px;
  margin-top: 1rem;
}

.checkboxes label:first-child {
  margin-top: 0;
}

input[type="checkbox"] {
  margin-right: 20px;
  /* hide styles */
  position: absolute;
  opacity: 0;
  height: 0;
  width: 0;
}

/* custom checkbox */
.checkmark {
  cursor: pointer;
  position: absolute;
  top: 0;
  left: -40px;
  height: 20px;
  width: 20px;
  border: 2px solid var(--light);
}

.checkmark:hover {
  border-color: var(--primary);
}

.checkmark.checked {
  background-color: var(--primary);
  border-color: var(--primary);
  background-image: url("/78f1494e4a49074799a890dc72789738.svg");
  background-repeat: no-repeat;
  background-position: center center;
  background-size: 16px;
}

/* STRENGTH METER */
.strength-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 2rem;
  padding: 24px 32px;
  background-color: var(--black);
}

.strength-container * {
  margin: 0;
}

.strength-word {
  font-size: 16px;
  color: var(--grey);
  text-transform: uppercase;
}
.strength-indicator {
  display: flex;
  align-items: center;
}
.strength-text {
  font-size: 0.9rem;
  text-transform: uppercase;
}

/* BARS */
.bars {
  margin-left: 1rem;
  display: flex;
  justify-content: space-between;
}

.bar {
  box-sizing: border-box;
  border: 2px solid white;
  height: 28px;
  width: 10px;
  margin-right: 8px;
}

.bar:last-child {
  margin: 0;
}

/* BUTTON */
button {
  display: block;
  width: 100%;
  height: 56px;
  border: none;
  background-color: var(--primary);
  font-family: inherit;
  font-size: 1rem;
  margin-top: 16px;
}

button:hover {
  border: 2px solid var(--primary);
  background-color: transparent;
  color: var(--primary);
  cursor: pointer;
}

button:hover img {
  filter: invert(88%) sepia(29%) saturate(425%) hue-rotate(65deg)
    brightness(120%) contrast(103%);
}

@media only screen and (min-width: 450px) {
  body {
    font-size: 18px;
    padding: 1rem;
  }

  .app-title {
    font-size: 24px;
  }
  .password {
    margin-bottom: 32px;
  }

  .password > input {
    font-size: 1.7rem;
  }

  section {
    padding: 19px 32px;
  }
  form {
    padding: 32px;
  }

  button {
    margin-top: 32px;
  }
}

